head.script:
window.onload = function () {

	var body = document.getElementsByTagName("body")[0];
	var p = document.createElement("p");
	p.setAttribute("class", "test");
	var text = document.createTextNode("This paragraph has a " + p.getAttributeNode("class").name + " attribute with value " 
	+ p.getAttributeNode("class").value + ".");
	p.appendChild(text);
	body.appendChild(p);
	
	
};
